uniswap = w3.eth.contract(address=UNISWAP_ADDRESS, abi=UNISWAP_ABI)

def handle_event(event):
  try:
    tx = w3.toJSON(event).strip('"')
    tx = w3.eth.get_transaction(tx)
    if tx['to'] == UNISWAP_ADDRESS:
      print('[x] ----------------------------------------------------------------------------------------- -  -  -')
      for key, value in tx.items():
        if key == 'input':
          print(f'[+] {key}: {uniswap.decode_function_input(value)}')
        elif key in ['v', 'r', 'hash']:
          print(f'[+] {key}: {value.hex()}')
        else:
          print(f'[+] {key}: {value}')
  except Exception:
        del(tx)

async def log_loop(event_filter, poll_interval):
  while True:
    for event in event_filter.get_new_entries():
      handle_event(event)
    await asyncio.sleep(poll_interval)

def filter_pool(filter_type):
  tx_filter = w3.eth.filter(filter_type)
  loop = asyncio.new_event_loop()
  asyncio.set_event_loop(loop)
  try:
    asyncio.run(log_loop(tx_filter, 2))
  finally:
    loop.close()
    asyncio.set_event_loop(None)

def main():
  filter_pool('pending')

if __name__ == '__main__':
  main()
