weth    = w3.eth.contract(address='0x5300000000000000000000000000000000000004', abi=TOKEN_ABI)
router  = w3.eth.contract(address='0x80e38291e06339d10AAB483C65695D004dBD5C69', abi=ROUTER_ABI)
factory = w3.eth.contract(address='0x37BAc764494c8db4e54BDE72f6965beA9fa0AC2d', abi=FACTORY_ABI)

# --

# Params
# --------------------
type_hash = usdc.functions.PERMIT_TYPEHASH().call()     # == bytes.fromhex('6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9')
owner     = eoa.address
spender   = router.address
amount    = 1 * 10 ** 5                                 # .1 usdc
nonce     = usdc.functions.nonces(eoa.address).call()   # seems only supports token with built in permit
deadline  = 20000000000                                 # future for a while

struct_hash = keccak(
  encode(['bytes32','address','address','uint256', 'uint256', 'uint256'], [type_hash, owner, spender, amount, nonce, deadline])
)
domain_hash = usdc.functions.DOMAIN_SEPARATOR().call()  # 0xa34ffbaebb64b07fa810e17b16c379cf279e56d3c1ac82c9def56220f31622ea
_hash = keccak(encode_packed(['uint16','bytes32','bytes32'], [0x1901, domain_hash, struct_hash]))
sig   = eoa.unsafe_sign_hash(_hash.hex())

# ---

amount_min = 0
swap_data = encode(['address', 'address', 'uint8'], [usdc.address, eoa.address, 2])
pool = factory.functions.getPool(usdc.address, weth.address).call()
swap_with_permit = router.functions.swapWithPermit(
  [(
    [
      [ pool, swap_data, '0x'+(40 *'0'), b'' ] # steps
    ],
    usdc.address,
    amount
  )],                                          # paths
  amount_min,
  deadline,
  (
    usdc.address,
    amount,
    deadline,
    sig.v,
    bytes.fromhex(hex(sig.r)[2:]),
    bytes.fromhex(hex(sig.s)[2:])
  )                                           # permit
)

def sign_tx(tx, key):
  return w3.eth.account.sign_transaction(tx, private_key=key)

def send_tx(signed_tx):
  return w3.eth.send_raw_transaction(signed_tx.raw_transaction)

tx = {
  'from': eoa.address, 'value': 0, 'chainId': w3.eth.chain_id, 'gas': 500000,
  'gasPrice': w3.eth.gas_price * 2, 'nonce': w3.eth.get_transaction_count(eoa.address)
}


def main():

  swap_tx = swap_with_permit.build_transaction(tx)
  w3.eth.call(swap_tx)
  print('[-] Attempting swap...')
  tx_hash = send_tx(sign_tx(swap_tx, eoa.key))
  receipt = w3.eth.wait_for_transaction_receipt(tx_hash)
  print (f'[>] Hash of attempt: {tx_hash}\n[>] {receipt}')

if __name__ == '__main__':
  main()
