from net import con
w3 = con('POLYGON')

router = w3.eth.contract(address=ROUTER_ADDRESS, abi=ROUTER_ABI)
quoter = w3.eth.contract(address=QUOTER_ADDRESS, abi=QUOTER_ABI)
usdc = w3.eth.contract(address=USDC_ADDRESS, abi=ERC20_ABI)
wmatic = w3.eth.contract(address=WMATIC_ADDRESS, abi=ERC20_ABI)

from eth_abi.packed import encode_packed
# 0x0d500b1d8e8ef31e21c99d1db9a6444d3adf12700001f42791bca1f2de4661ed88a30c99a7a9449aa84174
# tokenOut,Fee,TokenIn
#path = encode_packed(['address', 'uint24', 'address'], [WMATIC_ADDRESS, FEE, USDC_ADDRESS])
#PROCESSED_PATH = f'0x{path.hex()}'

AMOUNT_OUT = 1*10**18
SQRT_PRICE_LIMIT = 0
FEE = 500

amount_in = quoter.functions.quoteExactOutputSingle(USDC_ADDRESS, WMATIC_ADDRESS, FEE, AMOUNT_OUT, SQRT_PRICE_LIMIT).call()
multiplier = 0.01 # 1% slippage
AMOUNT_IN_WITH_SLIPPAGE = amount_in + round(multiplier * amount_in)

DEADLINE = 2000000000

# account
from os import getenv
from dotenv import load_dotenv
load_dotenv()
KEY = getenv('TKEY')
EOA = getenv('TRON')
#----------------------

params = (USDC_ADDRESS, WMATIC_ADDRESS, FEE, EOA, DEADLINE, AMOUNT_OUT, AMOUNT_IN_WITH_SLIPPAGE, SQRT_PRICE_LIMIT)

tx = {
        'from': EOA,
        'value':0,
        'chainId': 137,
        'gas': 250000,
        'maxFeePerGas': w3.eth.gas_price * 2,
        'maxPriorityFeePerGas': w3.to_wei('40', 'gwei'),
        'nonce': w3.eth.get_transaction_count(EOA)

}

# helpers
def sign_tx(tx, key):
  sig = w3.eth.account.sign_transaction
  signed_tx = sig(tx, private_key=key)
  return signed_tx

def send_tx(signed_tx):
  w3.eth.send_raw_transaction(signed_tx.rawTransaction)
  tx_hash = w3.to_hex(w3.keccak(signed_tx.rawTransaction))
  return tx_hash

# driver
def main():
  approve = usdc.functions.approve(ROUTER_ADDRESS, AMOUNT_IN_WITH_SLIPPAGE).build_transaction(tx)
  print ('[-] Approving... ')
  tx_hash = send_tx(sign_tx(approve, KEY))
  receipt = w3.eth.wait_for_transaction_receipt(tx_hash)
  print (f'[+] approved: {tx_hash}\n[>] {receipt}')

  tx.update({'nonce': w3.eth.get_transaction_count(EOA)})
  swap = router.functions.exactOutputSingle(params).build_transaction(tx)
  print('[-] Attempting swap...')
  tx_hash = send_tx(sign_tx(swap, KEY))
  receipt = w3.eth.wait_for_transaction_receipt(tx_hash)
  print (f'[>] Hash of attempt: {tx_hash}\n[>] {receipt}')

if __name__ == '__main__':
  main()
